    private void btnAgregarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAgregarActionPerformed
 
       String nombre = txtNombre.getText(); // Obtiene el texto del JTextField
String nombres = etqLista.getText(); // Obtiene los nombres actuales de la JLabel

if (nombres.toLowerCase().contains(nombre.toLowerCase())) {
    JOptionPane.showMessageDialog(null, "Nombre ya existente", "Error", JOptionPane.ERROR_MESSAGE);
    txtNombre.setText("");
} else {
    etqLista.setText(nombres+"  "+ nombre+" "); // Agrega el nombre a la JLabel
    txtNombre.setText(""); // Limpia el JTextField despu√©s de agregar el nombre
}

        // TODO add your handling code here:
    }//GEN-LAST:event_btnAgregarActionPerformed

    private void btnQuitarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnQuitarActionPerformed
        String nombres = etqLista.getText(); // Obtiene el texto actual de la JLabel
        String nombre2= txtNombre.getText();
       
        if(!nombres.toLowerCase().contains(nombre2.toLowerCase())){
            JOptionPane.showMessageDialog(null,"Nombre no encontrado", "Escriba bien el nombre",JOptionPane.ERROR_MESSAGE);
        txtNombre.setText("");
        }
         
        if(nombres.toLowerCase().contains(nombre2.toLowerCase())){
        nombres = nombres.replaceAll("(?i)"+nombre2, ""); // Elimina el nombre deseado de la cadena
        etqLista.setText(nombres);
        txtNombre.setText("");
         }
        
          
    }//GEN-LAST:event_btnQuitarActionPerformed

